ObjectStates.LuaScript:
function onObjectLeaveContainer(container, obj)
    if container == self then
        Wait.condition(function()
            obj.setColorTint(obj.held_by_color)
        end, 
        function() 
            if obj.held_by_color then return true else return false end
        end, 10)
    end
end

function onObjectLeaveContainer(container, obj)
    if container == self then
      obj.setGMNotes("Action marker")
        Wait.condition(function()
            obj.setColorTint(obj.held_by_color)
        end,
        function()
            if obj.held_by_color then return true else return false end
        end, 10)
    end
end

